import React, { Component } from 'react';
import BootstrapTable from 'react-bootstrap-table-next';
import axios from 'axios';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';
import paginationFactory from 'react-bootstrap-table2-paginator';

const formatDate = (dateTimeString) => {
  const date = new Date(dateTimeString);
  return date.toISOString().split("T")[0]; // Get only the date part
};

export class Bootstraptab1 extends Component {
  state = {
    enquiry: [], // Initial empty array
    columns: [
      {
        dataField: 'enquiry_id',
        text: 'Enq.Id',
        sort: true // Enable sorting for this column
      },
      {
        filter: textFilter(),
        dataField: 'enquirer_name',
        text: 'Enquirer Name',
        sort: true // Enable sorting for this column
      },
      {
        dataField: 'student_name',
        text: 'Student Name',
        sort: true // Enable sorting for this column
      },
      {
        dataField: 'enquirer_mobile',
        text: 'Phone',
        sort: true // Enable sorting for this column
      },
      {
        dataField: 'follow_up_date',
        text: 'Follow-up Date',
        formatter: formatDate,
        sort: true // Enable sorting for this column
      },
      {
        dataField: 'enquirer_email_id',
        text: 'Email',
        sort: true // Enable sorting for this column
      },
      {
        text: 'Actions', // Button column header
        dataField: 'actions', // Provide a unique field name
        formatter: (cell, row) => {
          return <button onClick={() => this.handleActionButtonClick(row)}>Display</button>;
        },
      },
    ],
  };

  componentDidMount() {
    axios.get('http://localhost:8080/api/egetall').then(response => {
      this.setState({
        enquiry: response.data,
      });
    });
  }

  handleActionButtonClick = (rowData) => {
    // Handle button click for a specific row
    console.log('Button clicked for row:', rowData);
  };

  render() {
    const options = {
      page: 1,
      sizePerPage: 10,
      totalSize: this.state.enquiry.length,
    };

    return (
      <div className="container">
        <br />
        <div className="container" style={{ marginTop: 10 }}>
          {this.state.enquiry.length > 0 ? (
            <BootstrapTable
              striped
              hover
              keyField='enquiry_id'
              data={this.state.enquiry}
              columns={this.state.columns}
              filter={filterFactory()}
              pagination={paginationFactory(options)}
            />
          ) : (
            <p>Loading data...</p>
          )}
        </div>
      </div>
    );
  }
}

export default Bootstraptab1;
