import React, { Component } from 'react';
import axios from 'axios';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';

class DataList extends Component {
  state = {
    data: [],
    searchTerm: '',
    currentPage: 1,
    totalSize: 0,
  };

  componentDidMount() {
    this.fetchData();
  }

  fetchData = () => {
    const { searchTerm, currentPage } = this.state;

    axios.get(`/api/data?page=${currentPage}&search=${searchTerm}`)
      .then(response => {
        const { content, totalPages, totalElements } = response.data;
        this.setState({
          data: content,
          totalSize: totalElements,
        });
      })
      .catch(error => {
        console.error(error);
      });
  };

  handleSearchChange = event => {
    this.setState({ searchTerm: event.target.value, currentPage: 1 }, this.fetchData);
  };

  handlePageChange = (page, sizePerPage) => {
    this.setState({ currentPage: page }, this.fetchData);
  };

  render() {
    const { data, searchTerm, currentPage, totalSize } = this.state;

    const columns = [ /* Define your table columns */ ];
    const options = {
      page: currentPage,
      sizePerPage: 10,
      totalSize,
      onPageChange: this.handlePageChange,
    };

    return (
      <div>
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={this.handleSearchChange}
        />
        <BootstrapTable
          keyField="id"
          data={data}
          columns={columns}
          pagination={paginationFactory(options)}
        />
      </div>
    );
  }
}

export default DataList;
