import React, { useState } from "react";
import "./AddBatchForm.css"; // Add your custom CSS file for styling
import { useNavigate } from "react-router-dom";

export function AddEnquiry() {
    const navigate = useNavigate();
  const [Enquiry, setBatchData] = useState({
    enquirer_name: "",
    batch_name: "",
    batch_start_time: "",
    batch_end_time: "",
    final_presentation_date: "",
    batch_fees: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBatchData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/api/new_enquiry", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(Enquiry),
    })
      .then((response) => {
        if (response.ok) {
          console.log("Batch added successfully");
          // You can reset the form or perform any other necessary actions here
        } else {
          console.error("Error adding batch");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });

      navigate(-1);
  };

  return (
    <div className="add-batch-form">
      <h2>Add New Batch</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Enquirer Name:</label>
          <input
            type="text"
            name="enquirer_name"
            // value={batchData.batch_id}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Student Name:</label>
          <input
            type="text"
            name="student_name"
            // value={batchData.batch_name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Mobile No</label>
          <input
            type="text"
            name=" enquirer_mobile"
            // value={batchData.batch_start_time}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input
            type="text"
            name="enquirer_email_id"
            // value={batchData.batch_end_time}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Enquiry Date</label>
          <input
            type="date"
            name="enquiry_date"
            // value={batchData.final_presentation_date}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Address</label>
          <input
            type="textfield"
            name="enquirer_address"
            // value={batchData.batch_fees}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Enquiry Query:</label>
          <input
            as="textfield"
            name="enquirer_query"
            // value={batchData.batch_fees}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <button type="submit">Add Batch</button>
        </div>
      </form>
    </div>
  );
}

export default AddEnquiry;
