import React, { Component } from 'react'
import BootstrapTable from 'react-bootstrap-table-next';
import axios from 'axios';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';
import paginationFactory from 'react-bootstrap-table2-paginator';

export class Bootstraptab1 extends Component {
  state = {
    enquiry: [], columns: [
      // {filter: textFilter()},
      {

        dataField: ' enquiry_id',
        text: 'Enq.Id'
      },
      {

        dataField: 'Name',

        text: 'Enquirer Name',

        

      }, {

        dataField: 'Age',

        text: 'Student Name',

        sort: true

      },

      {

        dataField: 'Address',

        text: 'Phone',

        sort: true

      },

      {

        dataField: 'City',

        text: 'Course',

        sort: true

      },
      {
        dataField: 'ContactNum',
        text: 'Follow-up-date',
        sort: true
      },
      {
        dataField: 'Salary',
        text: 'Salary',
        sort: true
      },
      {
        dataField: 'Department',
        text: 'Staff Name',
        sort: true
      }]
  }
  componentDidMount() {
    axios.get('http://localhost:8080/api/egetall').then(response => {
      console.log(response.data);

      this.setState({

        enquiry: response.data
      });

    });

  }
  render() {

    const options = {
      page: 2,
      sizePerPageList: [{
        text: '5', value: 5
      }, {
        text: '10', value: 10
      }, {
        text: 'All', value: this.state.enquiry.length
      }],
      sizePerPage: 5,
      pageStartIndex: 0,
      paginationSize: 3,
      prePage: 'Prev',
      nextPage: 'Next', firstPage: 'First',
      lastPage: 'Last',
      paginationPosition: 'top'
    };
    return (
      <div className="container">
        <div class="row" className="hdr">
          <div class="col-sm-14 btn btn-info">
            staff
          </div>
        </div>
        <div className="container" style={{ marginTop: 50 }}>
          <BootstrapTable
            striped
            hover
            keyField='id'
            data={this.state.enquiry}
            columns={this.state.columns}
            filter={filterFactory()}
            pagination={paginationFactory(options)} />
        </div>
      </div>
    )
  }
}
export default Bootstraptab1
