import React, { Component } from 'react';
import BootstrapTable from 'react-bootstrap-table-next';
import axios from 'axios';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';
import paginationFactory from 'react-bootstrap-table2-paginator';

export class Bootstraptab1 extends Component {
  state = {
    enquiry: [], // Initial empty array
    columns: [
      {
        dataField: 'enquiry_id',
        text: 'Enq.Id',
        sort: true
      },
      {
        filter: textFilter(),
        dataField: 'enquirer_name',
        text: 'Enquirer Name',
      },
      {
        
        dataField: 'student_name',
        text: 'Student Name',
      },
      {
        
        dataField: 'enquirer_mobile',
        text: 'Phone',
      },
      {
        
        dataField: 'follow_up_date',
        text: 'follow_up_date',
      },
      {
        
        dataField: 'staff_name',
        text: 'staff Name',
      },
      // ... other column definitions ...
    ],
  };

  componentDidMount() {
    axios.get('http://localhost:8080/api/egetall').then(response => {
      this.setState({
        enquiry: response.data,
      });
    });
  }

  render() {
    const options = {
        // page: 2,
        // sizePerPageList: [{
        //   text: '5', value: 5
        // }, {
        //   text: '10', value: 10
        // }, {
        //   text: 'All', value: this.state.enquiry.length
        // }],
        // sizePerPage: 5,
        // pageStartIndex: 0,
        // paginationSize: 3,
        // prePage: 'Prev',
         nextPage: 'Next', firstPage: 'First',
         lastPage: 'Last',
        // paginationPosition: 'top'
    };

    return (
      <div className="container">
        <div className="row hdr">
          <div className="col-sm-14 btn btn-info">staff</div>
        </div>
        <div className="container" style={{ marginTop: 50 }}>
          {this.state.enquiry.length > 0 ? (
            <BootstrapTable
              striped
              hover
              keyField='enquiry_id'
              data={this.state.enquiry}
              columns={this.state.columns}
              filter={filterFactory()}
              pagination={paginationFactory(options)}
            />
          ) : (
            <p>Loading data...</p>
          )}
        </div>
      </div>
    );
  }
}

export default Bootstraptab1;
