import React, { Component } from 'react';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import axios from 'axios';

const formatDate = (dateTimeString) => {
  const date = new Date(dateTimeString);
  return date.toISOString().split("T")[0];
};

class EnquiryPage extends Component {
  state = {
    enquiryData: [],
    columns: [
      // Define your columns here
    ],
  };

  componentDidMount() {
    this.fetchEnquiryData();
  }

  fetchEnquiryData = () => {
    axios.get('http://localhost:8080/api/enquiry') // Replace with your API endpoint
      .then(response => {
        this.setState({
          enquiryData: response.data,
        });
      })
      .catch(error => {
        console.error(error);
      });
  };

  render() {
    const { enquiryData, columns } = this.state;

    const options = {
      page: 1,
      sizePerPage: 10,
      totalSize: enquiryData.length,
    };

    return (
      <div>
        <h1>Enquiry List</h1>
        <button onClick={this.fetchEnquiryData}>Add Enquiry</button>
        <BootstrapTable
          keyField='enquiry_id'
          data={enquiryData}
          columns={columns}
          pagination={paginationFactory(options)}
        />
      </div>
    );
  }
}

export default EnquiryPage;
